import Hero from "./Hero";

//link for searching movie:https://api.themoviedb.org/3/search/movie?query=Dog&include_adult=false&language=en-US&page=1

const Search = ({ searchText, searchResults }) => {

  const results=searchResults.map((data)=>{
    return(
      <div className="container">
        <div className="row">
          <div className="col m-5 lead">
           <div>{data.title}</div>
          </div>
        </div>
      </div>
    )

  })

  return (
    <>
      <Hero text={`searching for ${searchText}`} />
      {results}
    </>
  );
};

export default Search;
