import React from "react";


class Item extends React.Component{

    //constructor
    constructor(props){
        super(props)

        this.state={
            clicks: 0
        }
    }

    clickMe(){
        //set state 
        this.setState({
            clicks : this.state.clicks +1
        })

    }


    render(){
        return (
            `${this.props.name} had been clicked 
            ${this.state.clicks}`
        )
    }
}



export  default Item;
