import Hero from "./Hero";
import { Link } from "react-router-dom/cjs/react-router-dom.min";

//link for searching movie:https://api.themoviedb.org/3/search/movie?query=Dog&include_adult=false&language=en-US&page=1

const Search = ({ searchText, searchResults }) => {
  const results = searchResults.map((response) => {
    return (
      <>
        <div className="col col-sm-12" key={response.id}>
          <div className="card m-3 p-0" style={{ width: "18rem" }}>
            <img
              src={`https://image.tmdb.org/t/p/w500/${response.poster_path}`}
              className="card-img-top"
              alt="..."
            />
            <div className="card-body">
              <h5 className="card-title text-dark">{response.title}</h5>
              <p className="card-text text-dark">
                Some quick example text to build on the card title and make up
                the bulk of the cardâ€™s content.
              </p>
              <Link to="#" className="btn btn-primary">
                Movie details
              </Link>
            </div>
          </div>
        </div>
      </>
    );
  });

  return (
    <>
      <Hero text={`searching for ${searchText}`} />

      <div className="container">
        <div className="row justify-content-center ">{results}</div>
      </div>
    </>
  );
};

export default Search;
