import React from "react";


class Item extends React.Component{

    //constructor
    constructor(props){
        super(props)

        this.state={
            clicks: 0
        }
    }

    clickMe(){
        //set state 
        this.setState({
            clicks : this.state.clicks +1
        })

    }


    render(){
        return (
            <h1 onClick={()=> this.clickMe()}>`${this.props.name} had been clicked 
            ${this.state.clicks}`</h1>
        )
    }
}



export  default Item;
