import React from "react";
import "./StarWars.css"; // Create this CSS file

class AffiliationItem extends React.Component {
  render() {
    return <li>{this.props.aff}</li>;
  }
}

class StarWars extends React.Component {
  constructor() {
    super();
    this.state = {
      image: null,
      name: "",
      height: null,
      homeworld: null,
      affiliations: [],
      isLoading: false, // Add loading state
    };
  }

  generateCharacter() {
    this.setState({ isLoading: true }); // Start loading
    
    const randomNumber = Math.round(Math.random() * 87);
    
    fetch(`https://akabab.github.io/starwars-api/api/id/${randomNumber}.json`)
      .then((response) => response.json())
      .then((data) => {
        this.setState({
          image: data.image,
          name: data.name,
          height: data.height,
          homeworld: data.homeworld,
          affiliations: data.affiliations || [], // Fallback for empty affiliations
          isLoading: false, // End loading
        });
      })
      .catch(() => {
        this.setState({ isLoading: false });
      });
  }

  render() {
    const { image, name, height, homeworld, affiliations, isLoading } = this.state;

    const affiliationItems = affiliations.map((aff, index) => (
      <AffiliationItem key={`${aff}-${index}`} aff={aff} />
    ));

    return (
      <div className="star-wars-container">
        <div className={`character-image-container ${isLoading ? "loading" : ""}`}>
          {image && (
            <img
              src={image}
              alt={name}
              className="character-image"
              onLoad={() => this.setState({ isLoading: false })}
            />
          )}
          {isLoading && <div className="loading-spinner"></div>}
        </div>

        <h1 className="character-name">{name || "Unknown Character"}</h1>
        
        <div className="character-details">
          {height && <p>Height: {height} m</p>}
          {homeworld && (
            <p>
              Homeworld <br />
              {homeworld}
            </p>
          )}
        </div>

        {affiliations.length > 0 && (
          <div className="affiliations">
            <h3>Affiliations</h3>
            <ul>{affiliationItems}</ul>
          </div>
        )}

        <button
          onClick={() => this.generateCharacter()}
          className="generate-btn"
          disabled={isLoading}
        >
          {isLoading ? "Loading..." : "Generate Character"}
        </button>
      </div>
    );
  }
}

export default StarWars;
