import React from "react";


class StarWars extends React.Component{

    //create state
    constructor(){
        super()
        this.state={
            image:null,
            height:null,
            homeworld:null,
            addiliations:null,
        }
    }

    //update state every time a click event is created
    generateCharacter(){
        //api request
        fetch("https://akabab.github.io/starwars-api/api/id/2.json")
        .then(Response => Response.json())
        .then(data =>{
            
        console.log(data)

        this.setState({
            image:this.state.image,
            height:data.height,
            homeworld:data.homeworld,
            affiliations:this.state.affiliations,
        })
        })

        
    }

    render()
    {
        return(
        
           <div>
           <img src=" " alt="Characterimage">{this.state.image}</img> 
            <h1> name</h1>
            <p>height: {this.state.height} m</p>
            <span> homeworld{this.state.homeworld}</span>
            <ul>affiliations
                <li>{this.state.affiliations}</li>
            </ul>
            <button onClick={()=> this.generateCharacter()}
            type="buton" 
            className="btn" >Generate Character</button>
           </div>
           
        )
    }
}

export default StarWars
