import React from "react";



class StarWars extends React.Component{
//create state
//create a constructor
    constructor(){
        super()
        this.state={
            generatedCharacter:false,
            name:null,
            height:null,
            mass:null,
            homeworld:null,
            films:[],
        }        
    }   
//set state
    generateCharacter(){
        //using the fecth api
        const number=Math.random() *83
        fetch("https://swapi.dev/api/people/${randomNumber}/")
            .then(Response=> Response.json())
            .then(data=>{
                console.log(data)

            this.setState({
            generatedCharacter:true,
            name: data.name,
            height:data.height,
            mass:data.mass,
            films:data.films,
        })
               
            });
        
    } 
    render(){
        return (
        <div>
            {/* create another set of curly braces and wrap the div you want to hide by default*/}
            {
                //if a certain state(generatedCharcter) is true
                this.state.generatedCharacter &&
                <div>
                    <h1>Name:{this.state.name} </h1>
                    <p>Height: {this.state.height} cm</p>
                    <p>Mass: {this.state.mass}kg</p>
                    <p>Homeworld:{this.state.homeworld}</p>
                    <ul>Films
                    <li>{this.state.films[0]}</li> 
                    <li>{this.state.films[1]}</li> 
            </ul>
                </div>
            }
            
            <button type="button"
            onClick={()=> this.generateCharacter()} 
            className="btn" >
                Randomize Character
            </button>
        </div>)
    }

}


export default StarWars;
