import React from "react";


class Item extends React.Component{

    //constructor
    constructor(props){
        super(props)

        this.state={
            clicks: 0,
            remainingClicks:100,
        }
    }

    clickMe(){
        //set state 
        this.setState({
            clicks : this.state.clicks +1,
            
        })

    }

    render(){
        return (
            <div>
            <h1 onClick={()=> this.clickMe()}>Hello {this.props.name}</h1>
            <span>has been clicked {this.state.clicks} times </span>
            </div>
           
        )
    }
}



export  default Item;
