import React from "react";

class FilmItemRow extends React.Component{

    render(){
        return(
            <div>
                  <a href={url}>
                    <li>
                    {this.props.url}
                  </li>
                    </a>
            </div>
          
        )
    } 
}

class StarWars extends React.Component{
//create state
//create a constructor
    constructor(){
        super()
        this.state={
            generatedCharacter:false,
            name:null,
            height:null,
            mass:null,
            homeworld:null,
            films:[],
        }        
    }   
//set state
    generateCharacter(){
        //using the fecth api
        const number=Math.random() *83
        const randomNumber=Math.round(number)

        fetch(`https://swapi.dev/api/people/${randomNumber}/`)
            .then(Response=> Response.json())
            .then(data=>{
                console.log(data)

            this.setState({
            generatedCharacter:true,
            name: data.name,
            height:data.height,
            mass:data.mass,
            homeworld:data.homeworld,
            films:data.films,
        })
               
            });
        
    } 
    render(){

        const movies=this.state.films.map((url,index)=>{
            return(
                <FilmItemRow key={index} url={url}/>
            )
        })
        return (
        <div>
            {/* create another set of curly braces and wrap the div you want to hide by default*/}
            {
                //if a certain state(generatedCharcter) is true.if statement in react
                this.state.generatedCharacter &&
                <div>
                    <h1>Name:{this.state.name} </h1>
                    <p>Height: {this.state.height} cm</p>
                    <p>Mass: {this.state.mass}kg</p>
                    <p><a href={this.state.homeworld}> Homeworld</a></p>
                    <ul>films
                    {/* using a component as film */}
                    {movies}
                    </ul>
                </div>
            }
            
            <button type="button"
            onClick={()=> this.generateCharacter()} 
            className="btn" >
                Randomize Character
            </button>
        </div>)
    }

}


export default StarWars;
